/* Стили для всех поисковых виджетов */
.dd-search-container {
	border: 1px solid #ccc;
	position: relative;
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	border-radius: 5px;
	margin: 0;
	background: #ffffff;
	background: -moz-linear-gradient(top, #ffffff 0%, #ffffff 50%, #f7f7f5 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #ffffff), color-stop(50%, #ffffff), color-stop(100%, #f7f7f5));
	background: -webkit-linear-gradient(top, #ffffff 0%, #ffffff 50%, #f7f7f5 100%);
	background: -o-linear-gradient(top, #ffffff 0%, #ffffff 50%, #f7f7f5 100%);
	background: -ms-linear-gradient(top, #ffffff 0%, #ffffff 50%, #f7f7f5 100%);
	background: linear-gradient(to bottom, #ffffff 0%, #ffffff 50%, #f7f7f5 100%);
	filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ffffff', endColorstr='#f7f7f5', GradientType=0);
	overflow: hidden;
	color: #999;
}
.dd-search-container a {
	color: #0094cb;
}
.dd-search-container a:hover {
	color: #cc0000;
}

.dd-icon-search {
	background-repeat: no-repeat;
	background-image: url('data:image/png;base64,');
}

/* Стили для всех вертикальных поисковых виджетов */
.dd-search-vertical-container {
	width: 240px;
	height: 400px;
	padding: 39px 25px;
}
.dd-search-vertical-title {
	text-align: center;
	font-size: 15px;
	color: #7c7c7c;
}
.dd-search-vertical-footer {
	position: absolute;
	left: 0;
	right: 0;
	bottom: 0;
	height: 39px;
	background: #fff;
	border-top: 1px solid #ccc;
	font-size: 11px;
	text-align: center;
	padding-top: 7px;
	line-height: 12px;
}

/* Стили поискового виджета клиник */
.dd-clinic-button-container {
	text-align: center;
	padding-top: 16px;
}
.dd-search-vertical-title-clinic {
	padding: 22px;
}
.dd-select-container-clinic {
	margin-bottom: 14px;
}