.search-mosglavmed-180x180 {
	width: 180px !important;
	height: 180px !important;
	padding: 12px !important;
	background: #fff !important;
	margin: 0 !important;
}

.search-mosglavmed-180x180 .dd-title {
	color: #498ff1;
}

.search-mosglavmed-180x180 .dd-title:before,
.search-mosglavmed-180x180 .dd-select:after {
	background-image: url('data:image/png;base64,');
}

.search-mosglavmed-180x180 .dd-title:before {
	background-position: 0 -16px;
}

.search-mosglavmed-180x180 .dd-label {
	display: none;
}

.search-mosglavmed-180x180 .dd-select:after {
	background-position: 0 0;
}

.search-mosglavmed-180x180 .dd-select select {
	font-size: 11px;
	padding-left: 5px;
}

.search-mosglavmed-180x180 .dd-submit-container {
	text-align: center;
}

.search-mosglavmed-180x180 .dd-submit {
	color: #fff;
	background: #81cec6;
	background: -moz-linear-gradient(top,  #81cec6 0%, #3f9799 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#81cec6), color-stop(100%,#3f9799));
	background: -webkit-linear-gradient(top,  #81cec6 0%,#3f9799 100%);
	background: -o-linear-gradient(top,  #81cec6 0%,#3f9799 100%);
	background: -ms-linear-gradient(top,  #81cec6 0%,#3f9799 100%);
	background: linear-gradient(to bottom,  #81cec6 0%,#3f9799 100%);
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#81cec6', endColorstr='#3f9799',GradientType=0 );
}

.search-mosglavmed-180x180 .dd-select,
.search-mosglavmed-180x180 .dd-input,
.search-mosglavmed-180x180 .dd-title,
.search-mosglavmed-180x180 .dd-submit-container {
	margin-left: 0 !important;
	margin-right: 0 !important;
	width: 158px !important;
}