.search-psihomed-225x225 {
	width: 225px;
	height: 225px;
}

.search-psihomed-225x225 .dd-title {
	color: #9b422a;
	padding: 15px 0;
}

.search-psihomed-225x225 .dd-title:before,
.search-psihomed-225x225 .dd-select:after {
	background-image: url('data:image/png;base64,');
}

.search-psihomed-225x225 .dd-title:before {
	background-position: 0 -16px;
}

.search-psihomed-225x225 .dd-label {
	display: none;
}

.search-psihomed-225x225 .dd-select:after {
	background-position: 0 0;
}

.search-psihomed-225x225 .dd-select {
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	border-radius: 5px;
}

.search-psihomed-225x225 .dd-submit-container {
	text-align: center;
}

.search-psihomed-225x225 .dd-submit {
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	border-radius: 5px;
	margin-top: 5px;
	color: #fff;
	text-shadow: 1px 1px 1px #000f23;
	font-weight: bold;
	height: 32px;
	padding: 0 25px;
	background: #70b5ef;
	background: -moz-linear-gradient(top,  #70b5ef 0%, #529edd 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#70b5ef), color-stop(100%,#529edd));
	background: -webkit-linear-gradient(top,  #70b5ef 0%,#529edd 100%);
	background: -o-linear-gradient(top,  #70b5ef 0%,#529edd 100%);
	background: -ms-linear-gradient(top,  #70b5ef 0%,#529edd 100%);
	background: linear-gradient(to bottom,  #70b5ef 0%,#529edd 100%);
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#70b5ef', endColorstr='#529edd',GradientType=0 );
}