.search-vlanamed-225x400 {
	width: 225px;
	height: 400px;
	padding-top: 20px;
}

.search-vlanamed-225x400 * {
	color: #2F4F4F;
}

.search-vlanamed-225x400 .dd-title {
	padding: 20px 0;
}

.search-vlanamed-225x400 .dd-title:before,
.search-vlanamed-225x400 .dd-select:after {
	background-image: url('data:image/png;base64,');
}

.search-vlanamed-225x400 .dd-title:before {
	background-position: 0 -16px;
}

.search-vlanamed-225x400 .dd-label {
	display: none;
}

.search-vlanamed-225x400 .dd-select {
	-webkit-border-radius: 8px;
	-moz-border-radius: 8px;
	border-radius: 8px;
}

.search-vlanamed-225x400 .dd-select:after {
	background-position: 0 0;
}

.search-vlanamed-225x400 .dd-select select {
	padding-left: 7px;
}

.search-vlanamed-225x400 .dd-submit-container {
	text-align: center;
}

.search-vlanamed-225x400 .dd-submit {
	margin-top: 15px;
	color: #fff;
	-webkit-border-radius: 8px;
	-moz-border-radius: 8px;
	border-radius: 8px;
	background: #128645;
	height: 36px;
	padding: 0 30px;
	text-transform: uppercase;
	background: -moz-linear-gradient(top,  #128645 0%, #1abb5d 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#128645), color-stop(100%,#1abb5d));
	background: -webkit-linear-gradient(top,  #128645 0%,#1abb5d 100%);
	background: -o-linear-gradient(top,  #128645 0%,#1abb5d 100%);
	background: -ms-linear-gradient(top,  #128645 0%,#1abb5d 100%);
	background: linear-gradient(to bottom,  #128645 0%,#1abb5d 100%);
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#128645', endColorstr='#1abb5d',GradientType=0 );
}

.search-vlanamed-225x400 .dd-search-vertical-footer a {
	color: #006400;
}

.search-vlanamed-225x400 .dd-search-vertical-footer a:hover {
	text-decoration: none;
}