.dd-widget-rs {
	zoom: 1;
	position: relative;
	overflow: hidden;
	font: normal 12px Arial, Helvetica, sans-serif;
}

.dd-widget-rs form {
	border: 0;
	outline: none;
	margin: 0;
	padding: 0;
}

.dd-widget-rs div.dd-error-request {
	color: #F00;
	font-size: 12px;
	display: none;
}

.dd-widget-rs .dd-success-message {
	color: #04a904;
	display: none;
}

.dd-widget-rs .dd-error-message {
	color: #F00;
	display: none;
}

.dd-widget-rs .dd-phone-request-empty, .dd-widget-rs .dd-phone-request-incorrect {
	margin-left: 205px;
}

.dd-widget-rs .dd-pic {
	position: absolute;
	width: 16px;
	height: 16px;
	left: 9px;
	top: 50%;
	margin: -8px 0 0 0;
	background-repeat: no-repeat;
}

.dd-widget-rs .dd-bg-user {
	background-image: url('data:image/png;base64,');
}

.dd-widget-rs .dd-bg-phone {
	background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAGtJREFUeNqkk8sJADEIRKMN2X8ZVuSSgxCCO0YVAiL4/E3IzNbE2B1Vtf1agDOxCuEoWIHwGloIEBEqASoJvx2ckPYOOhBG879A0iXeGrmhhKSMOvAiUAfoOg5PhZSd+EmJEcRjNP3OnwADAGBdO/nTq2s+AAAAAElFTkSuQmCC');
}

.dd-widget-rs .dd-r,
.dd-widget-rs .dd-small,
.dd-widget-rs .dd-docdoc {
	display: none;
}

.dd-widget-rs .dd-button {
	text-shadow: none;
}

.dd-widget-rs a {
	border: 0;
}